function ajax_upvote_link(){$(".ajax_upvote_link").on("click",function(e){e.stopPropagation();e.preventDefault();var upvote_link=$(this);$.ajax($(this).attr("href"),{error:function(jqxhr,status,error){alert("Could not upvote, an error occurred. Please try again.")},success:function(data,status,jqxhr){var resp=$.parseJSON(data);if(resp["error"]){alert(resp["error"]);return}upvote_link.children(".value").text(resp["votes"]+" votes")}})})}function hackpad_form(){$(function(){var link_form=$(".link_hackpad_url");link_form.hide();var modify_form=$("#modify_hackpad_url");modify_form.val(link_form.val());modify_form.keyup(function(e){$("#hackpad_iframe").attr("src",$(this).val())})})}function nav_bar(){$(function(){$("#account_settings_btn").click(function(e){$("#user_modal").modal("show");e.stopPropagation()});$("#navigation_button").click(function(e){$("#navigation_button").toggleClass("active");$("#navigation_items").collapse("toggle")})})}function vote_button(){$(function(){$(".vote_button").on("click",function(e){window.location=$(this).closest(".vote_button").children("li").children("a").attr("href");e.stopPropagation()})})}

