function hackpad_form(){$(function(){var link_form=$(".link_hackpad_url");link_form.hide();var modify_form=$("#modify_hackpad_url");modify_form.val(link_form.val());modify_form.keyup(function(e){$("#hackpad_iframe").attr("src",$(this).val())})})}function nav_bar(){$(function(){$("#account_settings_btn").click(function(e){$("#user_modal").modal("show");e.stopPropagation()});$("#navigation_button").click(function(e){$("#navigation_button").toggleClass("active");$("#navigation_items").collapse("toggle")})})}function vote_button(){$(function(){$(".vote_button").on("click",function(e){window.location=$(this).closest(".vote_button").children("li").children("a").attr("href");e.stopPropagation()})})}

